package com.zf.musicweb.dao;import com.zf.musicweb.entity.Collect;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.springframework.stereotype.Repository;import java.util.List;/** * <p> *  Mapper 接口 * </p> * * @author ${author} * @since 2022-01-05 */@Mapper@Repositorypublic interface CollectMapper extends BaseMapper<Collect> {    int existSongId(@Param("userId") Integer userId, @Param("songId") Integer songId);    int deleteCollect(@Param("userId") Integer userId, @Param("songId") Integer songId);    List<Collect> allCollect();    List<Collect> collectionOfUser(Integer userId);}